import styled from 'styled-components';

const Select = styled.select`
  width: 100%;
  padding: 12px 20px;
  margin: 8px 0;
  display: inline-block;
  border: 1px solid #ccc;
  border-radius: 4px;
  box-sizing: border-box;
`;
export const P = styled.p`
  color: red;
  font-size: 12px;
  margin: auto;
`;

export default Select;
